<script lang="ts">
import type { PropType } from 'vue'

export default {
  props: {
    size: {
      type: Number,
      default: 125,
    },
    direction: {
      type: String as PropType<'row' | 'col'>,
      default: 'row',
    },
    variant: {
      type: String as PropType<'primary' | 'mixed' | 'accent' | 'white' | 'black'>,
      default: 'black',
    },
  },
  computed: {
    textColorFill(): string {
      switch (this.variant) {
        case 'accent':
          return 'fill-accent'
        case 'white':
          return 'fill-white'
        case 'black':
          return 'fill-black'
      }
      return 'fill-primary'
    },
    mixedColorFill(): string {
      switch (this.variant) {
        case 'mixed':
          return 'fill-accent'
      }
      return this.textColorFill
    },
    dotColorFill(): string {
      switch (this.variant) {
        case 'white':
          return 'fill-primary'
        case 'black':
          return 'fill-white'
      }
      return 'fill-white'
    },
  },
}
</script>
